INSERT INTO `tb_perguntas` (`co_seq_perguntas`, `co_usuario`, `tx_pergunta`, `tx_resposta`, `nm_inclusao`, `dt_inclusao`, `nm_alteracao`, `dt_alteracao`, `st_registro`) VALUES
(1, 10, '<b>Como ser a renovao do contrato do meu Carto Q Sade vantagens?</b>', '<br>A renovao ser automtica.Aps a confirmao do pagamento da Taxa Administrativa Anual.<br><br>Caso no queira renovar seu carto, voc dever comunicar,expressamente, no prazo de at 30 dias antecedentes  finalizao do contrato.', 'Ele mesmo', '2017-11-01 14:40:49', NULL, '2017-11-23 10:43:32', 1),
(2, 10, '<b>Tenho que solicitar um novo Carto Q Sade Vantangens quando eu renovar o Contrato?</b>', '<br>No.<br><br>Aps a confirmao do pagamento da anuidade, seu cadastro ser atualizado, automaticamente, em nosso sistema. E voc poder utilizar o mesmo carto.', 'Ele mesmo', '2017-11-01 14:54:39', NULL, '2017-11-23 10:47:02', 1),
(3, 10, '<b>Posso colocar outro dependente no meu contrato,aps efetivado ?</b>', '<br>Caso 1:<br>Voc pode.Mas, s se no tiver ultrapassado o nmero de dependentes acordado em contrato. Observe qual contrato foi preenchido. <br>&nbsp;<br>Caso 2:\n<br>No voc no pode. Se voc j tiver includo os dependentes estipulado (s) por direito em contrato.', 'Ele mesmo', '2017-11-01 14:58:37', NULL, '2017-11-08 18:08:01', 1),
(4, 10, '<b>Posso trocar de titular ou dependente ao longo do meu contrato?</b>', '<br>No  possvel.<br> importante que, antes da efetivao do contrato, voc tenha certeza do nome do titular e ou seus dependente (s).', 'Ele mesmo', '2017-11-01 14:59:40', NULL, '2017-11-08 18:08:15', 1),
(5, 10, '<b>Posso cancelar meu Carto Q sade vantagens ?</b>', '<br>Sim.<br>Para o cancelamento,  necessrio comparecer a nossa Sede.\nNo Endereo: QS 03 LOTE 03, 05, 07 Sobreloja 113 - Edifcio Ptio Capital - guas Claras, Braslia - DF.\nPonto de referncia: Ao lado da FEIBOX e Taguatinga Shopping  Pisto Sul - Taguatinga - DF', 'Ele mesmo', '2017-11-01 15:05:08', NULL, '2017-11-08 18:08:31', 1),
(6, 10, '<b>Quantas vezes posso utilizar meu Carto Q Sade Vantagens ?</b>', '<br>Sempre, que for necessrio poder ser utilizado.', 'Ele mesmo', '2017-11-01 15:06:41', NULL, '2017-11-08 18:08:45', 1),
(7, 10, '<b>Posso utilizar meu Carto Q Sade Vantagens e</b><b>m qualquer lugar</b><b>?</b>', '<br>No. <br>A utilizao dever ser somente na rede Credenciada,voc poder obter a informao de quais locais no prprio Site.', 'Ele mesmo', '2017-11-01 15:11:28', NULL, '2017-11-09 11:43:28', 1),
(8, 10, '<b>Quando posso alterar o (s) nome (s) de um dependente no meu contrato?</b>', '<br>O (s) nome (s) s poder ser alterado, quando for feito a renovao do contrato. Aps 12 meses, voc poder solicitar  a devida alterao.<br>Isso dever ocorrer, sempre,30 dias antes do prazo da renovao findar.', 'Ele mesmo', '2017-11-01 16:37:17', NULL, '2017-11-23 10:42:14', 1),
(9, 10, '<b>Com o Carto Q Sade Vantagens terei direito de us-lo em consultas de emergncias ?</b>', 'No.&nbsp;<br>O atendimento  somente em clnicas cadastradas, e no ser atendimento de urgncia, apenas consultas e exames agendados, previamente, atravs de nossa Central Telefnica.', 'Ele mesmo', '2017-12-14 17:45:11', NULL, '2017-12-14 17:45:42', 1),
(10, 10, '<b>Poderei usar o meu&nbsp;&nbsp;Carto Q Sade + Vantagens, para internao hospitalar de qualquer espcie ?&nbsp;</b>', 'No.<br>Todo atendimento pelo Carto Q sade Vantagens se limita apenas s Consultas Ambulatoriais,Exames Diagnsticos e Laboratorias, todos previamente, agendados, em nossa Central Telefnica.<br>', 'Ele mesmo', '2017-12-14 17:50:52', NULL, NULL, 1);
