INSERT INTO `tb_perguntas` (`co_seq_perguntas`, `co_usuario`, `tx_pergunta`, `tx_resposta`, `nm_inclusao`, `dt_inclusao`, `nm_alteracao`, `dt_alteracao`, `st_registro`) VALUES
(1, 10, '<b>Como será a renovação do contrato do meu Cartão Q Saúde vantagens?</b>', '<br>A renovação será automática.Após a confirmação do pagamento da Taxa Administrativa Anual.<br><br>Caso não queira renovar seu cartão, você deverá comunicar,expressamente, no prazo de até 30 dias antecedentes à finalização do contrato.', 'Ele mesmo', '2017-11-01 14:40:49', NULL, '2017-11-23 10:43:32', 1),
(2, 10, '<b>Tenho que solicitar um novo Cartão Q Saúde Vantangens quando eu renovar o Contrato?</b>', '<br>Não.<br><br>Após a confirmação do pagamento da anuidade, seu cadastro será atualizado, automaticamente, em nosso sistema. E você poderá utilizar o mesmo cartão.', 'Ele mesmo', '2017-11-01 14:54:39', NULL, '2017-11-23 10:47:02', 1),
(3, 10, '<b>Posso colocar outro dependente no meu contrato,após efetivado ?</b>', '<br>Caso 1:<br>Você pode.Mas, só se não tiver ultrapassado o número de dependentes acordado em contrato. Observe qual contrato foi preenchido. <br>&nbsp;<br>Caso 2:\n<br>Não você não pode. Se você já tiver incluído os dependentes estipulado (s) por direito em contrato.', 'Ele mesmo', '2017-11-01 14:58:37', NULL, '2017-11-08 18:08:01', 1),
(4, 10, '<b>Posso trocar de titular ou dependente ao longo do meu contrato?</b>', '<br>Não é possível.<br>É importante que, antes da efetivação do contrato, você tenha certeza do nome do titular e ou seus dependente (s).', 'Ele mesmo', '2017-11-01 14:59:40', NULL, '2017-11-08 18:08:15', 1),
(5, 10, '<b>Posso cancelar meu Cartão Q saúde vantagens ?</b>', '<br>Sim.<br>Para o cancelamento, é necessário comparecer a nossa Sede.\nNo Endereço: QS 03 LOTE 03, 05, 07 Sobreloja 113 - Edifício Pátio Capital - Águas Claras, Brasília - DF.\nPonto de referência: Ao lado da FEIBOX e Taguatinga Shopping – Pistão Sul - Taguatinga - DF', 'Ele mesmo', '2017-11-01 15:05:08', NULL, '2017-11-08 18:08:31', 1),
(6, 10, '<b>Quantas vezes posso utilizar meu Cartão Q Saúde Vantagens ?</b>', '<br>Sempre, que for necessário poderá ser utilizado.', 'Ele mesmo', '2017-11-01 15:06:41', NULL, '2017-11-08 18:08:45', 1),
(7, 10, '<b>Posso utilizar meu Cartão Q Saúde Vantagens e</b><b>m qualquer lugar</b><b>?</b>', '<br>Não. <br>A utilização deverá ser somente na rede Credenciada,você poderá obter a informação de quais locais no próprio Site.', 'Ele mesmo', '2017-11-01 15:11:28', NULL, '2017-11-09 11:43:28', 1),
(8, 10, '<b>Quando posso alterar o (s) nome (s) de um dependente no meu contrato?</b>', '<br>O (s) nome (s) só poderá ser alterado, quando for feito a renovação do contrato. Após 12 meses, você poderá solicitar  a devida alteração.<br>Isso deverá ocorrer, sempre,30 dias antes do prazo da renovação findar.', 'Ele mesmo', '2017-11-01 16:37:17', NULL, '2017-11-23 10:42:14', 1),
(9, 10, '<b>Com o Cartão Q Saúde Vantagens terei direito de usá-lo em consultas de emergências ?</b>', 'Não.&nbsp;<br>O atendimento é somente em clínicas cadastradas, e não será atendimento de urgência, apenas consultas e exames agendados, previamente, através de nossa Central Telefônica.', 'Ele mesmo', '2017-12-14 17:45:11', NULL, '2017-12-14 17:45:42', 1),
(10, 10, '<b>Poderei usar o meu&nbsp;&nbsp;Cartão Q Saúde + Vantagens, para internação hospitalar de qualquer espécie ?&nbsp;</b>', 'Não.<br>Todo atendimento pelo Cartão Q saúde Vantagens se limita apenas às Consultas Ambulatoriais,Exames Diagnósticos e Laboratorias, todos previamente, agendados, em nossa Central Telefônica.<br>', 'Ele mesmo', '2017-12-14 17:50:52', NULL, NULL, 1);
